const int MAX = 100000;
int N, n;
vector<int> graph[MAX];
int label[MAX];

int dfs(int node){
	label[node] = -1;
	int used = 0, more = 0;
	for (int i = 0; i < graph[node].size(); ++i){
		int next = graph[node][i];
		if (!label[next]){
			int next_used = dfs(next);
			more |= used & next_used;
			used |= next_used;
		}
	}

	more |= more >> 1;
	more |= more >> 2;
	more |= more >> 4;
	more |= more >> 8;
	more |= more >> 16;
	++more;

	int possible = (~used) & (-more);
	label[node] = possible & (-possible);

	used |= label[node];
	used &= -label[node];
	return used;
}

