pair<int, int> finder(vector<vector<int> > G, int from) {
  int n = G.size();
  vector<int> dist(n, -1);
  dist[from] = 0;
  queue<int> Q;
  Q.push(from);
  pair<int, int> ret;
  while (!Q.empty()) {
    int now = Q.front();
    ret = pair<int, int>(now, dist[now]);
    Q.pop();
    for (int i=0; i<G[now].size(); i++)
      if (dist[G[now][i]] == -1) {
        dist[G[now][i]] = dist[now] + 1;
        Q.push(G[now][i]);
      }
  }
  return ret;
}

