int n;
vector<int> G[100010];
int dp[100010];
int s[100010];
void dfs(int v,int p){
	s[v] = 0;
	dp[v] = 0;
	for(int u: G[v]){
		if(p == u)continue;
		dfs(u,v);
		int t = s[v]&s[u];
		rep(i,20){
			if((t>>i)&1){
				dp[v] = max( dp[v] , i+1 );
			}
			else if((t>>i)>0){
				t += 1<<i;
			}
		}
		s[v] = s[v]+s[u]-(t<<1);
	}
	while((s[v]>>dp[v])&1){
		s[v] -= 1<<dp[v];
		dp[v] ++;
	}
	s[v] += 1<<dp[v];
	//cout << v << " " << dp[v] << " " << s[v] << endl;
}

