#include <bits/stdc++.h>
using namespace std;

const int N = 1e5 + 10;
int n, d1, d2, dis[N], mark[N];
vector <int> adj[N];

void _dfs(int v, int c) {
	dis[v] = c;
	mark[v] = 1;
	for (int i : adj[v])
		if (!mark[i])
			_dfs(i, c + 1);
}

void dfs(int s) {
	memset(mark, 0, sizeof mark);
	_dfs(s, 0);
}

int main() {
	cin >> n;
	for (int i = 1; i < n; i++) {
		int v, u;
		cin >> v >> u;
		v--; u--;
		adj[v].push_back(u);
		adj[u].push_back(v);
	}

	dfs(0);
	for (int i = 0, mx = 0; i < n; i++)
		if (dis[i] > mx) {
			mx = dis[i];
			d1 = i;
		}
	dfs(d1);
	for (int i = 0, mx = 0; i < n; i++)
		if (dis[i] > mx) {
			mx = dis[i];
			d2 = i;
		}
	cout << (dis[d2] + 1) / 2 - 1;
}

#include <iostream>
#include <vector>
using namespace std;
#pragma warning (disable: 4996)

int N, A[1 << 17], B[1 << 17], dp[1 << 17], ret; vector<int>X[1 << 17], G[1 << 17];
bool used[1 << 17];

void dfs(int pos) {
	used[pos] = true;
	for (int i = 0; i < X[pos].size(); i++) {
		if (used[X[pos][i]] == true) continue;
		G[pos].push_back(X[pos][i]);
		dfs(X[pos][i]);
	}
}

int solve(int pos) {
	int r = 0, s = 0;
	for (int i = 0; i < G[pos].size(); i++) {
		int A = solve(G[pos][i]);
		if (A == 1) r++;
		if (A == 2) s++;
	}

	if (dp[pos] != -1) {
		if (r == 0) return 0;
		if (r == 2) return 2;
		return 1;
	}
	if (r == 0 && s <= 1) { dp[pos] = ret; return 1; }
	return 2;
}

int main() {
	//FILE *in = freopen("in1.txt", "r", stdin);
	cin >> N;
	for (int i = 1; i <= N - 1; i++) {
		cin >> A[i] >> B[i];
		X[A[i]].push_back(B[i]);
		X[B[i]].push_back(A[i]);
	}
	dfs(1);

	for (int i = 1; i <= N; i++) dp[i] = -1;

	while (true) {
		int cnt = 0; for (int i = 1; i <= N; i++) { if (dp[i] == -1) cnt++; }
		if (cnt == 0) {
			cout << ret - 1 << endl;
			break;
		}
		solve(1);
		ret++;
	}
	return 0;
}

