const int N = 1e5 + 1;
vint t[N];
bitset<N> added;
int siz[N];
int ans = 0;
int calcSize(int cur, int p = -1) {
    siz[cur] = 1;
    for (int i : t[cur])
        if (!added.test(i) && i != p)
            siz[cur] += calcSize(i, cur);
    return siz[cur];
}
void make_centroid(int cur, int h = 0) {
    ans = max(ans, h);
    int lim = calcSize(cur) / 2;
    bool again = 1;
    int pr = -1;
    while (again) {
        again = 0;
        for (int i : t[cur])
            if (i != pr && !added.test(i) && siz[i] > lim) {
                pr = cur;
                cur = i;
                again = 1;
                break;
            }
    }
    added.set(cur);
    for (int i : t[cur])
        if (!added.test(i))
            make_centroid(i, h + 1);
}

void run() {
    int n;
    get n;
    rep(i, 1, n) {
        int v, u;
        read(v, u);
        t[v].pb(u);
        t[u].pb(v);
    }
    make_centroid(1);
    put ans;
}
