  for(int i=0;i<n-1;i++){
    ll a,b;cin>>a>>b;
    g[a].push_back(b);
    g[b].push_back(a);
  }
  set res=solve(1,0);
  ll ans=0;
  for(auto x:res)ans=max(ans,x);
  cout<<ans<<endl;
  return 0;
}

