// EOT

const int MAXN = 1e5 + 5;
const int LOGN = 19;

int N;
vector<int> G[MAXN];

void input()
{
    read(N);
    for (int i = 1; i < N; ++i) {
        int u, v;
        read(u); read(v);
        G[u].push_back(v);
        G[v].push_back(u);
    }
}

int calc(int u, int f)
{
    int st = 0, mask = 0;
    for (int i = 0; i < SZ(G[u]); ++i) {
        int v = G[u][i];
        if (v == f)
            continue;
        int t = calc(v, u);
        if (mask & t)
            chkmax(st, 31 - __builtin_clz(mask & t));
        mask |= t;
    }
    while (mask >> st & 1) {
        ++st;
    }
    return (mask & (~0 ^ ((1 << st) - 1))) | (1 << st);
}

void solve()
{
    int ans = 31 - __builtin_clz(calc(1, 0));
    printf("%d\n", ans);
}

