// visible means there's no bigger label b/w path from node in subtree of v to v
int solve(int v, int p) { // returns mask of visible node labels
  int vis[30] = {0};
  int assign = 0; // minimum possible label for v
  for (int u : adj[v]) {
    if (u == p) continue;
    int mask = solve(u, v);
    for (int j = 0; j < 30; ++j) {
      if (mask & (1 << j)) {
        vis[j]++;
        if (vis[j] == 2) assign = max(assign, j + 1);
      }
    }
  }
  while (vis[assign] > 0) assign++;
  int ret = (1 << assign); // will be visible to parent
  for (int j = assign + 1; j < 30; ++j) {
    if (vis[j]) ret |= (1 << j); // will still be visible to parent
  }
  return ret;
}// when the answer for root is returned the tree is splitted into regions which are connected by single edges
// merge trees with smaller node labels to ones with larger
// in the end we will have a single tree with node label = largest node lable visible to root
