const int N = 1e5 + 500;
const int K = 30;

vector < int > v[N];

int obr[N], doc[N][K], par[N], ja[N], sol, n, deg[N];
queue < int > Q;

void ide(int x){
	for(int y : v[x]){
		if(!obr[y]){
			par[x] = y;
			v[x].erase(find(v[x].begin(), v[x].end(), y));
		}
	}
	for(int y : v[x]){
		for(int k = 0;k < K;k++)
			doc[x][k] |= doc[y][k];
	}
	for(;doc[x][ja[x]];ja[x]++){
		doc[x][ja[x]] = 0;
	}
	//printf("x = %d ja = %d par = %d\n", x, ja[x], par[x]);
	doc[x][ja[x]] = 1; obr[x] = 1;
	sol = max(sol, ja[x]);
}

int main(){
	scanf("%d", &n);
	for(int i = 1;i < n;i++){
		int x, y; scanf("%d%d", &x, &y);
		v[x].PB(y); v[y].PB(x);
		deg[x]++, deg[y]++;
	}
	for(int i = 1;i <= n;i++){
		if(deg[i] == 1)
			Q.push(i);
	}
	for(int i = 1;i <= n;i++){
		int gas = Q.front(); Q.pop();
		ide(gas); deg[par[gas]]--;
		if(deg[par[gas]] <= 1)
			Q.push(par[gas]);
	}
	printf("%d\n", sol);
	return 0;
}

