	    }
	}

	if (cnt == 0) {
	    U[v] = 0;
	    X[v].push_back(0);
	} else if (cnt >= 2) {
	    U[v] = ma+1;
	    X[v].push_back(ma+1);
	} else {
	    buf.resize(ma+2);
