      //cout<<"d2:"<<v<<endl;
      for(Int u:G[v]){
	if(used[u]) continue;
	belong[u]=T.size();
	T.emplace_back();
        used[u]=2;
	for(Int x:G[u]){	  
	  if(used[x]) continue;
	  cnt[x]--;
	  if(cnt[x]==1) q.emplace(x);
	}
      }
    };

