	  belong[u]=T.size();
	  T.emplace_back();
	  continue;
	}

	belong[u]=t;
	used[u]=1;
	disable2(u);
      }
    }

    //for(Int i=0;i<n;i++) cout<<i<<":"<<used[i]<<" "<<belong[i]<<endl;

