#define n	100005

int		N, D, Rt, Ans, st[n], Dep[n];

struct	Lin{
		int v, next;
}E[n << 1];

void	Link(int u, int v)	{
		E[++ D]= (Lin){v, st[u]};	st[u]= D;
		E[++ D]= (Lin){u, st[v]};	st[v]= D;
}

void	DFS(int u, int f)	{
		Dep[u]= Dep[f] + 1;

		for (int i= st[u], v; i; i= E[i].next)
			if	((v= E[i].v) != f)	DFS(v, u);
}

int		main(int argc, char* argv[]){
		N= IN();
		For(i, 2, N)	Link(IN(), IN());

		DFS(1, 0);

		Rt= 1;
		For(i, 2, N)	if	(Dep[i] > Dep[Rt])	Rt= i;

		DFS(Rt, 0);

		int	D= 0;
		For(i, 1, N)	D= max(D, Dep[i]);

		for (; D > 1; D>>= 1)	Ans++;
		cout << Ans << endl;

		return	0;
}

