struct scc_graph{
    // Find Strongly Connected Components of given graph
    //
    // Correctness tested on the following problem:
    // https://atcoder.jp/contests/arc069/tasks/arc069_d
    //
    int n;
    vector<vector<int> > graph;
    vector<vector<int> > inv_graph;
    vector<int> fr, to;
    vector<int> fn;
    vector<int> vis;
    vector<int> component;
    int col;
    void inv_dfs(int v)
    {
        vis[v] = 1;
        for (auto id : inv_graph[v])
        {
            int u = fr[id];
            if (!vis[u])
            {
                inv_dfs(u);
            }
        }
        fn.push_back(v);
    }
    void dfs(int v)
    {
        component[v] = col;
        for (auto id : graph[v])
        {
            int u = to[id];
            if (component[u] == -1)
            {
                dfs(u);
            }
        }
    }
    scc_graph(int _n)
    {
        n = _n;
        graph = vector<vector<int> >(n);
        inv_graph = vector<vector<int> >(n);
    }
    void add_edge(int v, int u)
    {
        graph[v].push_back(fr.size());
        inv_graph[u].push_back(fr.size());
        fr.push_back(v);
        to.push_back(u);
    }
    vector<vector<int> > scc()
    {
        vis = vector<int>(n);
        component = vector<int>(n, -1);
        fn = {};
        col = 0;
        for (int i = 0; i < n; i++)
        {
            if (!vis[i])
            {
                inv_dfs(i);
            }
        }
        reverse(fn.begin(), fn.end());
        for (auto i : fn)
        {
            if (component[i] == -1)
            {
                dfs(i);
                col++;
            }
        }
        vector<vector<int> > res(col);
        for (int i = 0; i < n; i++)
        {
            res[component[i]].push_back(i);
        }
        return res;
    }
};

int ans;

vector<int> solve(int v, int p, vvi &graph)
{
    vector<int> cnt(20);
    for (auto u : graph[v]) if (u != p)
    {
        vector<int> z = solve(u, v, graph);
        for (int j = 0; j < 20; j++) cnt[j] += z[j];
    }
    int sel = -1;
    for (int j = 0; j < 20; j++)
    {
        if (cnt[j] >= 2)
        {
            sel = j + 1;
        }
    }
    if (sel != -1)
    {
        for (int j = 0; j < sel; j++)
        {
            cnt[j] = 0;
        }
        ans = max(ans, sel);
        return cnt;
    }
    for (int j = 0; j < 20; j++)
    {
        if (cnt[j] == 0)
        {
            sel = j;
            break;
        }
    }
    cnt[sel]++;
    for (int j = 0; j < sel; j++)
    {
        cnt[j] = 0;
    }
    ans = max(ans, sel);
    return cnt;
}

