constexpr int MAX = 20;

class DUninity {
public:
    int N;
    vector<vector<int>> E;

    int dfs(int u, int p) {
        vector<int> CNT(MAX, 0);
        for (int v: E[u]) if (v != p) {
            int s = dfs(v, u);
            for (int i = 0; i < MAX; ++i) {
                if (s&1<<i) CNT[i]++;
            }
        }
        int ans = MAX;
        for (int i = MAX-1; i >= 0; --i) {
            if (CNT[i] >= 2) break;
            if (CNT[i] == 0) ans = i;
        }
        int cur = 1<<ans;
        for (int i = ans+1; i < MAX; ++i) if (CNT[i] == 1) cur |= 1<<i;
        return cur;
    }

    void solve(istream& cin, ostream& cout) {
        cin >> N;
        E.resize(N);
        for (int i = 0; i < N - 1; ++i) {
            int a, b; cin >> a >> b;
            --a; --b;
            E[a].push_back(b);
            E[b].push_back(a);
        }

        cout << logceil(dfs(0, -1)) - 1 << '\n';
    }
};


int main() {
	ios_base::sync_with_stdio(false); cin.tie(nullptr); cout.tie(nullptr);
	DUninity solver;
	std::istream& in(std::cin);
	std::ostream& out(std::cout);
	solver.solve(in, out);
    return 0;
}

