using namespace std;
#define MAXN 100010
int Adj[MAXN],V[MAXN*2],nxt[MAXN*2],c;
void AddEdge(int u,int v)
{
	c++;V[c]=v,nxt[c]=Adj[u];Adj[u]=c;
}
int dp[MAXN][21],n,ans;
void dfs(int u,int p)
{
	for(int i=Adj[u];i;i=nxt[i])
		if(V[i]!=p)
		{
			int v=V[i];
			dfs(v,u);
			for(int j=0;j<=20;j++)
				dp[u][j]+=dp[v][j];
		}
	int k=0;
	for(int i=20;i>=0;i--)
		if(dp[u][i]>1)
		{
			k=i+1;
			break;
		}
	while(dp[u][k]) k++;
	dp[u][k]++;
	for(int i=0;i<k;i++)
		dp[u][i]=0;
	ans=max(ans,k);
}
int main()
{
	scanf("%d",&n);
	int u,v;
	for(int i=1;i<n;i++)
	{
		scanf("%d%d",&u,&v);
		AddEdge(u,v);
		AddEdge(v,u);
	}
	dfs(1,0);
	printf("%d\n",ans);
}

