const int N = 100 * 1000 + 10;

vector<int> g[N];
bool del[N];
int size[N];
int max_s[N];
vector<int> cur;
int ans = 0;

void dfs(int v, int par) {
    size[v] = 1;
    max_s[v] = 0;
    cur.push_back(v);
    for (int to : g[v])
        if (!del[to] && to != par) {
            dfs(to, v);
            size[v] += size[to];
            max_s[v] = max(max_s[v], size[to]);
        }
}

void build(int st, int curh) {
    ans = max(ans, curh);
    cur.clear();
    dfs(st, st);
    if (cur.size() == 1)
        return;
    int tot = cur.size();
    vector<int> vs;
    for (int x : cur)
        if (max_s[x] * 2 <= tot && (tot - size[x]) * 2 <= tot)
            vs.push_back(x);
    random_shuffle(vs.begin(), vs.end());
    int v = vs[0];
    del[v] = true;
    for (int to : g[v])
        if (!del[to])
            build(to, curh + 1); 
}


int main() {
    ios_base::sync_with_stdio(0);
    cin.tie(nullptr);
    int n;
    cin >> n;
    for (int i = 0; i < n - 1; i++) {
        int a, b;
        cin >> a >> b;
        a--;
        b--;
        g[a].push_back(b);
        g[b].push_back(a);    
    }
    int res = n - 1;
    for (int rep = 0; rep < 15; rep++) {
        fill(del, del + n, false);
        ans = 0;
        build(0, 0);
        res = min(res, ans);        
    }
    cout << res << endl;
}

