WeightedTree<T> make2(int n, int offset = 1){
    WeightedTree<T> res(n);
    for(int i = 0;i < n-1 ; i++){
        int a,b ; cin >> a >> b;
        a -= offset, b -= offset;
        T c; cin >> c;
        res[a].emplace_back(b,c);
        res[b].emplace_back(a,c);
    }
    return res;
}

vector<int> Centers(vector<set<int>> &g,int idx = 0){
    vector<int> res;
    int max_depth = -1, leaf;
    auto dfs = [&](auto &&self,int x,int p,int d)->bool{
        bool flag = false;
        if(max_depth < d){
            max_depth = d, leaf = x, flag = true;
            res.clear();
        }
        for(auto e:g[x]){
            if(e != p)
            flag |= self(self,e,x,d+1);
        }
        if(flag and (d == (max_depth>>1) or d == ((max_depth+1)>>1))) {
            res.emplace_back(x);
        }
        return flag;
    };
    dfs(dfs,idx,-1,0);
    max_depth = -1;
    dfs(dfs,leaf,-1,0);
    return res;
}

signed main(){
    ios_base::sync_with_stdio(0),cin.tie(0),cout.tie(0);cout<<fixed<<setprecision(15);
    INT(n);
    vector<set<int>> g(n);
    rep(i,n-1){
        int a=in()-1,b=in()-1;
        g[a].emplace(b);g[b].emplace(a);
    }
    auto solve = [&](auto &&self,int x) -> int{
        if(g[x].size() == 0) return 0;
        auto centers = Centers(g,x);
        int res = 0;
        if(centers.size() == 1){
            int center = centers.back();
            for(auto e:g[center]){
                g[e].erase(center);
                chmax(res,self(self,e));
                g[e].emplace(center);
            }
            return res + 1;
        }
        else{
            int a = centers[0],b = centers[1];
            g[a].erase(b);
            g[b].erase(a);
            int A = self(self,a);
            int B = self(self,b);
            for(auto e:g[a]){
                g[e].erase(a);
                chmax(B,self(self,e));
                g[e].emplace(a);
            }
            for(auto e:g[b]){
                g[e].erase(b);
                chmax(A,self(self,e));
                g[e].emplace(b);
            }
            g[a].emplace(b);
            g[b].emplace(a);
            return min(A,B) + 1;
        }
    };
    cout << solve(solve,0) << endl;
}

