int N;
vector <int> E[100010];
int ans, d[100010];

void dfs(int x, int fa){
	int X = 0;
	vector <int> v;
	for(int e : E[x])if(e != fa){
		dfs(e, x);
		v.pb(d[e]);
		X |= d[e];
	}
	sort(all(v)); reverse(all(v));
	if(sz(v) == 0){
		d[x] = 1;
		return;
	}
	int s = 0;
	for(int i=19;i>=0;i--)if(1<<i & X){s = i; break;}
	int cnt = 0;
	for(int e : v)if(1<<s & e)cnt++;
	if(cnt > 1){
		d[x] = 1<<(s+1);
		return;
	}
	for(int i=0;i<s;i++)if(!(v[0] & 1<<i)){
		int cnt = 0;
		for(int e : v)if((1<<i) > e)cnt++;
		if(cnt == sz(v) - 1){
			for(int j=s;j>i;j--)if(v[0] & 1<<j)d[x] |= 1<<j;
			d[x] |= 1<<i;
			return;
		}
	}
	d[x] = 1<<(s+1);
}

