#define rep(i, n) for(UL i = 0; i < (n); i++)

struct Problem {

	void Solve() {
		UL N; cin >> N;
		vector<vector<UL>> E(N);
		rep(i, N - 1) {
			UL a, b; cin >> a >> b; a--; b--;
			E[a].push_back(b); E[b].push_back(a);
		}
		vector<UL> q;
		vector<bool> flag(N);
		stack<UL> q2;
		vector<UL> P(N);
		q2.push(0);
		while (q2.size()) {
			UL p = q2.top(); q2.pop();
			q.push_back(p); flag[p] = true;
			for (UL e : E[p]) {
				if (flag[e]) P[p] = e;
				else q2.push(e);
			}
		}
		vector<UL> dp(N);
		UL ans = 0;
		reverse(q.begin(), q.end());
		q.pop_back();
		rep(i, q.size()) {
			UL p = q[i];
			UL d = dp[p] + 1;
			ans = max(ans, dp[P[p]] + d);
			dp[P[p]] = max(dp[P[p]], d);
		}
		UL M = 0;
		UL uninity = 0;
		while (M < ans) { M = M * 2 + 2; uninity++; }
		cout << uninity << endl;
	}


	Problem();
};
int main() {
	unique_ptr<Problem> p(new Problem());
	p->Solve();
	return 0;
}
