void dfs(int cur, int par = -1, int len = 0) {
	h[cur] = len;
	for (auto to : adj[cur]) {
		if (vis[to] || to == par)
			continue;
		dfs(to, cur, len + 1);
	}
}

int getMaxNode(int cur, int par = -1) {
	int res = cur;
	for (auto to : adj[cur]) {
		if (vis[to] || to == par)
			continue;
		int nxt = getMaxNode(to, cur);
		if (h[res] < h[nxt])
			res = nxt;
	}
	return res;
}

int center(int cur, int par = -1) {
	if (h[cur] == tot / 2)
		return cur;
	int res = -1;
	for (auto to : adj[cur]) {
		if (vis[to] || to == par)
			continue;
		res = max(res, center(to, cur));
		if (res != -1)
			return res;
	}
	return res;
}

void solve(int cur, int curK = 1) {
	ans = max(ans, curK);
	dfs(cur);
	int n1 = getMaxNode(cur);
	dfs(n1);
	n1 = getMaxNode(n1);
	tot = h[n1];
	root = center(n1);
	vis[root] = 1;
	for (auto to : adj[root]) {
		if (vis[to])
			continue;
		solve(to, curK + 1);
	}
}

