const int maxn = 1e5 + 10;

int n;
vi g[maxn];
unsigned int dp[maxn];

void dfs(int u = 1, int par = 0) {
	unsigned int s1 = 0, s2 = 0, &s = dp[u];
	for (int v: g[u]) {
		if(v != par) {
			dfs(v, u);
			s2 |= (dp[v] & s1);
			s1 |= dp[v];
		}
	}
	s = 1;
	while(s <= s2) {
		s <<= 1;
	}
	while(s & s1) {
		s <<= 1;
	}
	s = ((s > 1) ? (s1 & ~(s - 1)) : s1) | s;
	return;
}

int main() {
	scf(n);
	rep (i, n - 1) {
		int u, v;
		scf(u, v);
		g[u].pb(v);
		g[v].pb(u);
	}
	dfs();
	unsigned int s = 1;
	int ans = -1;
	while(s <= dp[1]) {
		s <<= 1;
		++ans;
	}
	printf("%d\n", ans);
	return 0;
}

