#include <bits/stdc++.h>

using std::max;
using std::min;
const int N=1e5+10;

struct Edge{ int to,next; }way[N<<1];
int n,dep[N],num[N],tot,mans;

void Build(int a,int b) { way[++tot]=(Edge){b,num[a]}; num[a]=tot; }

void Init() {
	scanf("%d",&n);
	int a,b;
	for (int i=2;i<=n;++i) {
		scanf("%d%d",&a,&b);
		Build(a,b);
		Build(b,a);
	}
}

void Dfs_1(int x,int fa) {
	dep[x]=1;
	for (int i=num[x];i;i=way[i].next) {
		int v=way[i].to;
		if (v==fa) continue;
		Dfs_1(v,x);
		dep[x]=max(dep[x],dep[v]+1);
	}
}

void Dfs_2(int x,int fa,int uplen) {
	mans=min(mans,max(uplen,dep[x]));
	int m1=0,m2=0;
	for (int i=num[x];i;i=way[i].next) {
		int v=way[i].to;
		if (v==fa) continue;
		if (dep[v]>m1) m2=m1,m1=dep[v];
		else if (dep[v]>m2) m2=dep[v];
	}
	m1++; m2++;
	for (int i=num[x];i;i=way[i].next) {
		int v=way[i].to;
		if (v==fa) continue;
		if (dep[v]+1==m1) Dfs_2(v,x,max(m2,uplen)+1);
		else Dfs_2(v,x,max(m1,uplen)+1);
	}
}

void Solve() {
	Dfs_1(1,0);
	mans=n;
	Dfs_2(1,0,1);
	int cnt=0;mans--;
	while (mans) 
		mans>>=1,cnt++;
	printf("%d\n",cnt);
}

int main() {
	Init();
	Solve();
	return 0;
}

