using namespace std;

const int N = 2e5 + 7;
const int LOG = 25;

int n;
vector<int> adj[N];
int ans;

int dfs(int u, int from) {
	int all = 0, least = 0;
	for (int v : adj[u]) {
		if (v == from) continue;
		int mask = dfs(v, u);
		for (int j = 0; j < LOG; j++) {
			if ((all >> j & 1) && (mask >> j & 1)) {
				least = max(least, j + 1);
			}
		}
		all |= mask;
	}
	assert(least + 1 < LOG);
	for (int i = least; ; i++) {
		if (~all >> i & 1) {
			ans = max(ans, i);
			all ^= (1 << i);
			for (int j = i - 1; j >= 0; j--) {
				if (all >> j & 1) {
					all ^= (1 << j);
				}
			}
			return all;
		}
	}
}

int solve() {
	ans = 0;
	dfs(rand() % n + 1, 0);
	return ans;
}

int main() {
	scanf("%d", &n);
	//n = 1e5;
	for (int i = 1; i < n; i++) {
		int u, v;
		scanf("%d %d", &u, &v);
		adj[u].push_back(v);
		adj[v].push_back(u);
	}
	int ret = n;
	for (int r = 1; r <= 5; r++) {
		ret = min(ret, solve());
	}
	printf("%d\n", ret);
}

