#include <bits/stdc++.h>

const int N = 100005;

int head[N], tot, ans = 0, n;
struct edge { int to, nxt; } e[N << 1];

void link(int x, int y) {
	e[++tot] = (edge) { y, head[x] }, head[x] = tot;
	e[++tot] = (edge) { x, head[y] }, head[y] = tot;
}
int dfs(int x, int f = 0) {
	int ret = 0, max = 0;
	for (int i = head[x]; i; i = e[i].nxt) if (e[i].to != f) {
		int v = dfs(e[i].to, x);
		max = std::max(max, std::__lg(ret & v)), ret |= v;
	}
	int b = __builtin_ctz(~ret & (-1 << max));
	ans = std::max(ans, b);
	return (ret &= (-1 << b)) |= 1 << b;
}

int main() {
	std::ios::sync_with_stdio(0), std::cin.tie(0);
	std::cin >> n;
	for (int i = 1, x, y; i < n; ++i)
		std::cin >> x >> y, link(x, y);
	dfs(1), std::cout << ans << '\n';
	return 0;
}

