using namespace std;
typedef long long ll;

using namespace std;
#define N 100010
vector<ll> t[N];
//bool dead[N];
class DivideTree{
  public:
  bool dead[N];
  class FindCentroid{
      public:
      ll res,n;
      ll alt(ll x,ll from){
	  if(dead[x])return 0;
	  ll sum=1;
	  for(auto y:t[x]){
	      if(y!=from)sum+=alt(y,x);
	  }
	  return sum;
      }
      ll dfs(ll x,ll from){
	  if(dead[x])return 0;
	  ll sum=1,maxi=-1;
	  for(auto y:t[x]){
	      if(y==from)continue;
	      ll val=dfs(y,x);
	      sum+=val,maxi=max(maxi,val);
	  }
	  ll rem=n-sum;
	  maxi=max(maxi,rem);
	  if(maxi<=n/2)res=x;
	  return sum;
      }
      ll main(ll s){
	  n=alt(s,-1);
	  dfs(s,-1);
	  return res;
      }
  };FindCentroid fc;

  ll solve(ll s){
      //cout<<s<<":";
      s=fc.main(s);
      //cout<<s<<endl;
      dead[s]=1;
      ll res=-1;
      for(auto y:t[s]){
	  if(dead[y])continue;
	  res=max(res,solve(y));
      }
      dead[s]=0;
      return res+1;
  }
  ll main(){
      for(int i=0;i<N;i++)dead[i]=0;
      return solve(1);
  }
};DivideTree dt;

int main(){
    ll n,a,b;cin>>n;
    for(int i=0;i<n-1;i++){
	cin>>a>>b;
	t[a].push_back(b);
	t[b].push_back(a);
    }
    cout<<dt.main()<<endl;
    return 0;
}

