int dep[MAXN + 5], st[MAXN + 5], ed[MAXN + 5], dfn_cur = 0, seq[MAXN + 5];

inline void dfs_init(int u, int fa)
{
	dep[u] = dep[fa] + 1;

	seq[st[u] = ++dfn_cur] = dep[u];
	for(auto v : adj[u]) if(v != fa)
		dfs_init(v, u);
	ed[u] = dfn_cur;
}

namespace SEGT
{
	const int MAX_NODE = MAXN * 4;

	int mx[MAX_NODE + 5], add[MAX_NODE + 5];

	inline void push_up(int u) { mx[u] = max(mx[u << 1], mx[u << 1 | 1]); }
	inline void push_down(int u)
	{
		if(add[u])
		{
			mx[u << 1] += add[u], mx[u << 1 | 1] += add[u];
			add[u << 1] += add[u], add[u << 1 | 1] += add[u];
			add[u] = 0;
		}
	}

	inline void build(int u, int l, int r)
	{
		if(l == r)
		{
			mx[u] = seq[l];
			return;
		}

		int mid = (l + r) / 2;
		build(u << 1, l, mid);
		build(u << 1 | 1, mid + 1, r);
		push_up(u);
	}

	inline void update(int u, int l, int r, int ql, int qr, int val)
	{
		if(ql <= l && r <= qr)
		{
			mx[u] += val;
			add[u] += val;
			return;
		}
		push_down(u);

		int mid = (l + r) / 2;
		if(ql <= mid) update(u << 1, l, mid, ql, qr, val);
		if(mid < qr) update(u << 1 | 1, mid + 1, r, ql, qr, val);
		push_up(u);
	}
}

int ans;

inline void dfs_ans(int u, int fa)
{
	chkmin(ans, SEGT::mx[1]);

	for(auto v : adj[u]) if(v != fa)
	{
		++SEGT::mx[1], ++SEGT::add[1];
		SEGT::update(1, 1, n, st[v], ed[v], -2);

		dfs_ans(v, u);

		--SEGT::mx[1], --SEGT::add[1];
		SEGT::update(1, 1, n, st[v], ed[v], +2);
	}
}

inline void solve()
{
	dfs_init(1, 0);
	SEGT::build(1, 1, n);
	ans = n + 1, dfs_ans(1, 0);
	printf("%d\n", (int) ceil(log2(ans)));
}

