using Graph = vector<vector<int>>;
using Level = int;
using Upper = bool;
using Status = pair<Level, Upper>;
using Index = int;

Status get_status(vector<Status> statuses) {
    Status res;
    for (Status status : statuses)res = max(res, status);
    return res;
}

Index get_parent(const Graph& graph, set<Index> liftupped, const Index now) {
    for (Index next : graph[now])
        if (liftupped.find(next) == liftupped.end())
            return next;
    return -1;
}

Status next_status(Status status) {
    if (status.second) {
        return { status.first + 1,false };
    }
    else {
        return { status.first,true };
    }
}

int max_level(map<Level, int>& cnt) {
    int base = -1;
    for (auto itr : cnt)base = max(base, itr.first);
    return base;
}

int main()
{
    int N;
    cin >> N;
    Graph graph(N);

    rep(i, N - 1) {
        int a, b;
        cin >> a >> b;
        a--; b--;
        graph[a].push_back(b);
        graph[b].push_back(a);
    }

    vector<vector<Status>> raw_statuses(N);
    queue<Index> liftups;
    set<Index> liftupped;
    rep(i, N)if (graph[i].size() == 1) {
        raw_statuses[i].push_back({ 0,true });
        liftups.push(i);
        liftupped.insert(i);
    }
    while (!liftups.empty()) {
        Index now = liftups.front(); liftups.pop();
        Index parent = get_parent(graph, liftupped, now);
        if (parent == -1)break;
        Status status = get_status(raw_statuses[now]);
        Status parent_status = next_status(status);
        raw_statuses[parent].push_back(parent_status);

        if (raw_statuses[parent].size() + 1 == graph[parent].size()) {
            assert(liftupped.find(parent) == liftupped.end());
            liftups.push(parent);
            liftupped.insert(parent);
        }
    }
    map<Level, int> cnt;
    for (auto& raw_status : raw_statuses) {
        auto status = get_status(raw_status);
        cnt[status.first]++;
    }
    int level = max_level(cnt);
    if (cnt[level] > 1)level++;
    cout << level << endl;

    rep(i, N) {
        Status status = get_status(raw_statuses[i]);
        // cout << "Index: " << i << " -> (" << status.first << "," << status.second << ")" << endl;
    }
    return 0;
}

