{
	sta[u] = 0;

	int mask = 0;
	for (int v : adj[u])
		if (v != prev)
		{
			int sv = dfs(v, u);
			mask |= sv & sta[u];
			sta[u] |= sv;
		}

	if (sta[u] == 0)
		sta[u] = 1, lab[u] = 0;
	else
