using namespace std;

typedef pair <int, int> ii;

const int Maxn = 100005;

int n;
vector <int> neigh[Maxn];
int par[Maxn];
bool er[Maxn];

ii Get(int v, int p)
{
    par[v] = p;
    ii res = ii(1, v);
    for (int i = 0; i < neigh[v].size(); i++) {
        int u = neigh[v][i];
        if (u == p || er[u]) continue;
        ii g = Get(u, v);
        res = max(res, ii(g.first + 1, g.second));
    }
    return res;
}

int Solve(int v)
{
    ii g = Get(v, 0);
    g = Get(g.second, 0);
    int tk = g.first / 2;
    int u = g.second;
    while (tk--) u = par[u];
    er[u] = true;
    int mx = 0;
    for (int i = 0; i < neigh[u].size(); i++) {
        int nxt = neigh[u][i];
        if (!er[nxt]) mx = max(mx, Solve(nxt) + 1);
    }
    return mx;
}

