const int maxn = (int) 1e5 + 5;
int n;
vector<int> g[maxn];

int read() {
  if (scanf("%d", &n) < 1) {
    return false;
  }
  for (int i = 0; i < n; i++) {
    g[i].clear();
  }
  for (int i = 0; i < n - 1; i++) {
    int v, u;
    scanf("%d%d", &v, &u);
    v--;
    u--;
    g[v].push_back(u);
    g[u].push_back(v);
  }
  return true;
}

int del[maxn];
int d[maxn];

int dfs(int v, int p) {
  int res = v;
  for (int i = 0; i < sz(g[v]); i++) {
    int u = g[v][i];
    if (del[u] || u == p) {
      continue;
    }
    d[u] = d[v] + 1;
    int cur = dfs(u, v);
    if (d[cur] > d[res]) {
      res = cur;
    }
  }
  return res;
}

bool getPath(int v, int t, int p, vector<int> &path) {
  path.push_back(v);
  if (v == t) {
    return true;
  }
  for (int i = 0; i < sz(g[v]); i++) {
    int u = g[v][i];
    if (del[u] || u == p) {
      continue;
    }
    if (getPath(u, t, v, path)) {
      return true;
    }
  }
  path.pop_back();
  return false;
}

int rec(int v) {
  d[v] = 0;
  v = dfs(v, -1);
  d[v] = 0;
  int u = dfs(v, -1);
  vector<int> path;
  getPath(v, u, -1, path);
  v = path[sz(path) / 2];
  del[v] = true;
  int res = 0;
  for (int i = 0; i < sz(g[v]); i++) {
    u = g[v][i];
    if (!del[u]) {
      res = max(res, rec(u) + 1);
    }
  }
  return res;
}

void solve() {
  for (int i = 0; i < n; i++) {
    del[i] = false;
  }
  printf("%d\n", rec(0));
}

