using namespace std;

typedef long long ll;

int N;
vector<int> G[100010];

ll dp[100010];

ll DFS(int v,int p)
{
    ll AND = 0,OR = 0;
    if(G[v].size() == 1 && G[v][0] == p)return dp[v] = 1;
    for(int i = 0; i < G[v].size(); i++)
    {
        int ne = G[v][i];
        if(ne == p)continue;
        ll now = DFS(ne,v);
        AND |= (now & OR);
        OR |= now;
    }
    int id = 0;
    while(AND >= (1 << id))id++;
    while((OR >> id) & 1)id++;
    return dp[v] = (OR + (1 << id)) & (~((1 << id) - 1));
}

