const int N=100005;

struct E{
	int to,next;
}mem[N<<1];
int n,num,x,y,inf,ans;
int head[N],dp[N][2],d[N],f[N][2];

void add(int x,int y){
	num++;
	mem[num].to=y; mem[num].next=head[x];
	head[x]=num;
}

void dfs1(int k,int pre){
	int j,u,mx1,mx2; mx1=mx2=0;
	if (d[k]==1&&k>1){
		dp[k][0]=dp[k][1]=0;
		return;
	}
	for (j=head[k];j;j=mem[j].next){
		u=mem[j].to;
		if (u==pre) continue;
		dfs1(u,k);
		mx1=max(mx1,min(dp[u][0],dp[u][1]));
		mx2=max(mx2,dp[u][1]);
	}
	if ((k==1&&d[k]==1)||(k>1&&d[k]==2)){
		if (mx2) dp[k][0]=mx2;
		else dp[k][0]=mx2+1;
	}
	dp[k][1]=mx1+1;
	dp[k][0]=min(dp[k][0],mx1+1);
}

void dfs2(int k,int pre){
	int j,u,cnt,w;
	vector<int> son,mx[2];
	son.clear();
	mx[0].clear(); mx[1].clear();
	w=min(f[k][0],f[k][1]);
	for (j=head[k];j;j=mem[j].next){
		u=mem[j].to;
		if (u==pre) continue;
		son.push_back(u);
		mx[0].push_back(min(dp[u][0],dp[u][1]));
		mx[1].push_back(min(dp[u][0],dp[u][1]));
		w=max(w,min(dp[u][0],dp[u][1]));
	}
	ans=min(ans,w+1);
	cnt=son.size()-1;
	if (!cnt){
		u=son[0];
		if (k==1) f[u][0]=f[u][1]=0;
		else{
			if (f[k][1]) f[u][0]=f[k][1];
			else f[u][0]=f[k][1]+1;
			f[u][1]=f[k][0]+1;
		}
		dfs2(u,k);
		return;
	}
	for (j=1;j<=cnt;j++) mx[0][j]=max(mx[0][j],mx[0][j-1]);
	for (j=cnt-1;j>=0;j--) mx[1][j]=max(mx[1][j],mx[1][j+1]);
	for (j=0;j<=cnt;j++){
		u=son[j];
		w=min(f[k][0],f[k][1]);
		if (j>0) w=max(w,mx[0][j-1]);
		if (j<cnt) w=max(w,mx[1][j+1]);
		f[u][0]=f[u][1]=w+1;
		dfs2(u,k);
	}
}

