	}
	memset(dp,0x3f,sizeof(dp)); inf=dp[0][0];
	memset(f,0x3f,sizeof(f));
	dfs1(1,0);
	ans=min(dp[1][0],dp[1][1]);
	f[1][1]=f[1][0]=0;
	dfs2(1,0);
	printf("%d\n",ans);
	return 0;
}

