struct Node{
    vector<Node*> dests;
    void add(Node* n){
        dests.push_back(n);
    }
    void add2(Node* n){
        add(n);
        n->add(this);
    }
    pair<int, Node*> dfs(Node* from = nullptr){
        pair<int, Node*> ret = {0, this};
        for(Node* d : dests){
            if(d == from){ continue; }
            auto p = d -> dfs(this);
            p.first++;
            ret = max(ret, p);
        }
        return ret;
    }
    pair<int, Node*> dfs_(Node* from = nullptr){
        pair<int, Node*> ret = {0, this};
        for(Node* d : dests){
            if(d == from){ continue; }
            auto p = d -> dfs(this);
            p.first++;
            ret = max(ret, p);
        }
        return ret;
    }
};

pair<int, Node*> dfs2(Node* from){
    pair<int, Node*> ret = {0, from};
    vector<pair<Node*, int>> st = {{from, 0}};
    set<Node*> visited = {from};
    while(st.size()){
        auto n = st.back(); st.pop_back();
        auto node = n.first;
        pair<int, Node*> depth = {n.second, n.first};
        ret = max(ret, depth);
        for(Node* d : node->dests){
            if(visited.count(d)){ continue; }
            visited.insert(d);
            st.emplace_back(d, depth.first + 1);
        }
    }
    return ret;
}

