			if(sum[i]>1){
				i++;
				while(sum[i]){
					f[k]^=(1<<i);
					i++;
				}
				f[k]|=(1<<i);
				return;
			}
			f[k]|=(1<<i);
		}else if(o){
			int h=0,g;
			while(i>=0){
				if(!sum[i]) g=(1<<i)|h;
				else h|=(1<<i);
				if(sum[i]>1) break;
				i--;
			}
			f[k]|=g;
			return;
		}
	}
	f[k]=o+o;
	if(!f[k]) f[k]=1;
}
