vector<int> solve(int pos) {
	int used[30]; for (int i = 0; i < 30; i++) used[i] = 0;

	for (int i = 0; i < G[pos].size(); i++) {
		vector<int> E = solve(G[pos][i]);
		for (int j = 0; j < E.size(); j++) used[E[j]]++;
	}

	int c = -1, t = 0;
	for (int i = 0; i < 30; i++) { if (used[i] >= 2) t = i; }
	for (int i = t; i < 30; i++) {
		if (used[i] == false) { c = i; break; }
	}
	dp[pos] = c;
	vector<int>L; L.push_back(c);
	for (int i = c + 1; i < 30; i++) { if (used[i] == true) L.push_back(i); }
	return L;
}

