  vector<set> v;
  for(auto y:g[x])if(y!=from)v.push_back(solve(y,x));
  ll clash=-1;
  for(ll i=D;i-1;i--){
    int cnt=0;
    for(auto x:v)if(x.find(i)!=x.end())cnt++;
    if(cnt>=2){clash=i; break;}
  }
  ll gen;
  for(ll i=clash+1;;i++){
    int cnt=0;
    for(auto x:v)if(x.find(i)!=x.end())cnt++;
    if(cnt==0){gen=i; break;}
  }
  set res; res.insert(gen);
  for(ll i=gen+1;i<D;i++){
    int cnt=0;
    for(auto x:v)if(x.find(i)!=x.end())cnt++;
    if(cnt>0)res.insert(i);
  }
  return res;
}
