	for (int i=las[x],y;~i;i=E[i].nxt)
		if (!vis[y=E[i].y]&&y!=fa[x]){
			fa[y]=x;
			dfs(y,d+1);
		}
}
void solve(int x,int dep){
	ans=max(ans,dep);
	mx=-1;
	fa[x]=0;
	dfs(x,0);
	mx=-1;
	fa[w]=0;
	dfs(w,0);
	For(i,0,mx/2) w=fa[w];
	vis[w]=1;
	for (int i=las[w],y;~i;i=E[i].nxt)
		if (!vis[y=E[i].y]){
			solve(y,dep+1);
		}
}

