pii bfs (int v, const vvi &g)
{
    const int n = sz(g);
    vi d(n, -1);
    queue<int> q;

    q.push(v);
    d[v] = 0;
    int last = -1;

    while (!q.empty())
    {
        int cur = q.front();
        last = cur;
        q.pop();

        for (int dest : g[cur]) if (d[dest] == -1)
        {
            d[dest] = d[cur] + 1;
            q.push(dest);
        }
    }

    return mp(last, d[last]);
}

