    pii to = bfs(0, g);
    pii res = bfs(to.fst, g);
    int len = res.snd;

    int diam = 0;
    int curk = 0;

    while (diam < len)
    {
        curk++;
        diam = 2 * diam + 2;
    }

    cout << curk << '\n';
}

