      }
      for(Int i=0;i<n;i++)
	if(!used[i]) exit(0);
    }

    vector<Int> cnt(n);
    queue<Int> q;
    for(Int i=0;i<n;i++){
      cnt[i]=G[i].size();
      if(cnt[i]==1) q.emplace(i);
    }

    vector<Int> used(n,0);
    vector<Int> belong(n,-1);
    vector<vector<Int> > T;

    auto disable=[&](Int v){
      //cout<<"d1:"<<v<<endl;
      for(Int u:G[v]){
	if(used[u]) continue;
	cnt[u]--;
	if(cnt[u]==1) q.emplace(u);
      }
    };

    auto disable2=[&](Int v){
      //cout<<"d2:"<<v<<endl;
      for(Int u:G[v]){
	if(used[u]) continue;
        used[u]=2;
	belong[u]=T.size();
	T.emplace_back();
	for(Int x:G[u]){	  
	  if(used[x]) continue;
	  cnt[x]--;
	  if(cnt[x]==1) q.emplace(x);
	}
      }
    };

    while(!q.empty()){
      Int v=q.front();q.pop();
      if(used[v]) continue;
      Int t=T.size();
      T.emplace_back();

      Int c=-1;      
      for(Int u:G[v])
	if(!used[u]) c=u;

      disable(c);
      used[c]=3;
      belong[c]=t;
      for(Int u:G[c]){
	//cout<<c<<"->"<<u<<endl;
	if(used[u]) continue;

	if(cnt[u]>1){
	  disable(u);
	  used[u]=2;
	  belong[u]=T.size();
	  T.emplace_back();
	  continue;
	}

	used[u]=1;
	belong[u]=t;
	disable2(u);
      }
    }

    //for(Int i=0;i<n;i++) cout<<i<<":"<<used[i]<<" "<<belong[i]<<endl;
    for(Int i=0;i<n;i++){
      if(!used[i]) exit(0);
      if(belong[i]<0) exit(0);
      if(belong[i]>=(Int)T.size()) exit(0);
    }

    for(Int v=0;v<n;v++)
      for(Int u:G[v])
	if(belong[v]!=belong[u])
	  T[belong[v]].emplace_back(belong[u]);

    for(auto &v:T){
      sort(v.begin(),v.end());
      v.erase(unique(v.begin(),v.end()),v.end());
    }
    G=T;
    //break;
  }
  cout<<ans<<endl;
  return 0;
}

