		if ((y = to[i]) != px)
			dfs(y, x), s |= f[x] & f[y], f[x] |= f[y];
	s = lg2(s << 1 | 1), s += ctz(~(f[x] >> s));
	f[x] = (f[x] >> s | 1) << s, ans[x] = s;
}

