template< typename T >
using Edges = vector< edge< T > >;
template< typename T >
using WeightedTree = vector< Edges<T>>;
using tree = vector< vector<int> >;

tree make(int n,int offset = 1){
    tree res(n);
    for(int i = 0;i < n-1; i++){
        int a,b; cin >> a >> b;
        a -= offset,b -= offset;
        res[a].emplace_back(b);
        res[b].emplace_back(a);
    }
    return res;
}
template< typename T >
WeightedTree<T> make2(int n, int offset = 1){
    WeightedTree<T> res(n);
    for(int i = 0;i < n-1 ; i++){
        int a,b ; cin >> a >> b;
        a -= offset, b -= offset;
        T c; cin >> c;
        res[a].emplace_back(b,c);
        res[b].emplace_back(a,c);
    }
    return res;
}

vector<int> Centers(tree &g,int idx = 0){
    vector<int> res;
    int max_depth = -1, leaf;
    auto dfs = [&](auto &&self,int x,int p,int d)->bool{
        bool flag = false;
        if(max_depth < d){
            max_depth = d, leaf = x, flag = true;
            res.clear();
        }
        for(auto e:g[x]){
            if(e != p)
            flag |= self(self,e,x,d+1);
        }
        if(flag and (d == (max_depth>>1) or d == ((max_depth+1)>>1))) {
            res.emplace_back(x);
        }
        return flag;
    };
    dfs(dfs,idx,-1,0);
    max_depth = -1;
    dfs(dfs,leaf,-1,0);
    return res;
}

signed main(){
    ios_base::sync_with_stdio(0),cin.tie(0),cout.tie(0);cout<<fixed<<setprecision(15);
    INT(n);
    auto g = make(n);
    rep(i,n)sort(all(g[i]));
    auto solve = [&](auto &&self,int x) -> int{
        if(g[x].size() == 0) return 0;
        auto centers = Centers(g,x);
        int res = 0;
        if(centers.size() == 1){
            int center = centers.back();
            for(auto e:g[center]){
                auto it = lower_bound(all(g[e]),center);
                g[e].erase(it);
                chmax(res,self(self,e));
                g[e].eb(center);
            }
            return res + 1;
        }
        else{
            int a = centers[0],b = centers[1];
            auto it = lower_bound(all(g[a]),b);
            g[a].erase(it);
            it = lower_bound(all(g[b]),a);
            g[b].erase(it);
            int A = self(self,a);
            int B = self(self,b);
            for(auto e:g[a]){
                auto it = lower_bound(all(g[e]),a);
                g[e].erase(it);
                chmax(B,self(self,e));
            }
            for(auto e:g[b]){
                auto it = lower_bound(all(g[e]),b);
                g[e].erase(it);
                chmax(A,self(self,e));
            }
            g[a].eb(b);
            g[b].eb(a);
            return min(A,B) + 1;
        }
    };
    cout << solve(solve,0) << endl;
}

