void Add(int x,int y)
{
	e[++tot]=y; nxt[tot]=h[x]; h[x]=tot;
}

void dfs(int x,int y)
{
	int i,j,k;
	for (i=h[x],j=k=0;i;i=nxt[i]) if (e[i]!=y)
	{
		dfs(e[i],x);
		if ((j&f[e[i]])>0) k=max(k,g[j&f[e[i]]]+1);
		j|=f[e[i]];
	}
	for (i=0;((1<<i)&j)>0;i++);
	k=max(k,i);
	ans=max(ans,k); f[x]=1<<k;
	for (;g[j]>k;j^=(1<<g[j])) f[x]|=(1<<g[j]);
}

