const int N=1e5+9;

unsigned int bit[N];
int n,f[N],ans;
int to[N<<1],nxt[N<<1],beg[N],tot;

inline void add(int u,int v)
{
	to[++tot]=v;
	nxt[tot]=beg[u];
	beg[u]=tot;
}

inline void dfs(int u,int fa)
{
	bit[u]=f[u]=0;
	int las=0;
	for(int i=beg[u],v;i;i=nxt[i])
		if((v=to[i])!=fa)
		{
			dfs(v,u);
			las|=bit[v]&bit[u];
			bit[u]|=bit[v];
		}
	if(bit[u]==0)
		bit[u]=1;
	else
	{
		while((1<<f[u])<las || bit[u]&(1<<f[u]))f[u]++;
		bit[u]=(bit[u]>>f[u]<<f[u])|(1<<f[u]);
	}
	if(ans<f[u])ans=f[u];
}

