int nt[2*N],fs[N],dt[2*N],n,m,s[N],f[N][20],ans;
void link(int x,int y)
{
	nt[++m]=fs[x];
	dt[fs[x]=m]=y;
}
void dfs(int k,int fa)
{
	for(int i=fs[k];i;i=nt[i])
	{
		int p=dt[i];
		if(p!=fa)
		{
			dfs(p,k);
			fo(j,0,19) f[k][j]+=f[p][j];
		}
	}
	int lim=0;
	fod(j,19,0) if(f[k][j]>1)
	{
		lim=j+1;
		break;
	}
	while(f[k][lim]) lim++;
	s[k]=lim;
	fo(j,0,lim-1) f[k][j]=0;
	f[k][lim]++;
	ans=max(ans,s[k]);
}
