int dfs(int u,int f)
{
	int bit=0,over=0,pos;
	for (int p=FIR[u];p;p=NEXT[p])
	{
		int v=TO[p];
		if (v==f) continue;
		int tmp=dfs(v,u);
		over|=bit&tmp;
		bit|=tmp;
