int N, A[1 << 17], B[1 << 17], dp[1 << 17], ret; vector<int>X[1 << 17], G[1 << 17];
bool used[1 << 17];

void dfs(int pos) {
	used[pos] = true;
	for (int i = 0; i < X[pos].size(); i++) {
		if (used[X[pos][i]] == true) continue;
		G[pos].push_back(X[pos][i]);
		dfs(X[pos][i]);
	}
}

int solve(int pos) {
	int z = 0, r = 0;
	for (int i = 0; i < G[pos].size(); i++) { r += solve(G[pos][i]); }

	if (dp[pos] != -1) {
		if (r == 0) return 0;
		return 1;
	}
	if (r == 0) { dp[pos] = ret; return 1; }
	return 0;
}

