	for (int i = 1; i <= N; i++) dp[i] = -1;

	while (true) {
		int cnt = 0; for (int i = 1; i <= N; i++) { if (dp[i] == -1) cnt++; }
		if (cnt == 0) {
			cout << ret - 1 << endl;
			break;
		}
		solve(1);
		ret++;
	}
	return 0;
